package Bean;

public class Hours {
	private String Eid;

	private String Year;

	private int Kid;

	private int Credit;

	public Hours(String Year) {
		// TODO Auto-generated constructor stub
		this.Year = Year;
	}
	
	public String getEid() {
		return Eid;

	}

	public String getYear() {
		return Year;

	}

	public int getKid() {
		return Kid;

	}

	public int getCredit() {
		return Credit;

	}



	public void setEid(String Eid) {
		this.Eid = Eid;

	}

	public void setYear(String Year) {
		this.Year = Year;

	}

	public void setKid(int Kid) {
		this.Kid = Kid;

	}

	public void setCredit(int Credit) {
		this.Credit = Credit;

	}

	
	

}
